//
//  ViewController.swift
//  To Do List
//
//  Created by Chris Kelly on 18/12/20.
//

import UIKit

class ViewController: UIViewController {

    @IBOutlet var tableView: UITableView!
    
    
    // Hold all the tasks the user has entered
    var tasks = [String]()
    
    override func viewDidLoad() {
        super.viewDidLoad()
        
        // Give the Add page a title
        self.title = "Tasks"
        
        // Get all saved tasks
        tableView.delegate = self
        tableView.dataSource = self
        
        // Setup
        
        let defaults = UserDefaults.standard
        defaults.set(0, forKey: "count")
        defaults.set(0, forKey: "task")
        if !UserDefaults().bool(forKey: "setup") {
            UserDefaults().set(true, forKey: "setup")
            UserDefaults().set(0, forKey: "setup")
        }
        
        updateTasks()
        
    }
    func updateTasks() {
        
        
        // Remove all elements in the task array
        tasks.removeAll()
        
        guard let count = UserDefaults().value(forKey: "count") as? Int else {
            return
        }
        
        // Go through from 0 up until the count value
        for x in 0..<count {
            if let task = UserDefaults().value(forKey: "task_\(x+1)") as? String {
                tasks.append(task)
            }
        }
        
        // Reload the data inside the tableview
        tableView.reloadData()
        
    }
    @IBAction func didTapAdd() {
        
        let vc = storyboard?.instantiateViewController(identifier: "entry") as! EntryViewController
        vc.title = "New Task"
        vc.update = {
            // Prioritise the update by calling the function
            DispatchQueue.main.async {
                self.updateTasks()
            }
        }
        navigationController?.pushViewController(vc, animated: true)
    }
    

}


extension ViewController: UITableViewDelegate{
    
    func tableView(_ tableView: UITableView, didSelectRowAt indexPath: IndexPath) {
        // Index path comes in through the function, the pos in the table where the row is
        tableView.deselectRow(at: indexPath, animated: true)
        
        let vc = storyboard?.instantiateViewController(identifier: "task") as! TaskViewController
        vc.title = "New Task"
        vc.task = tasks[indexPath.row                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           